import * as tomato from "@po-to/tomato";
import * as tdom from "@po-to/tomato-jquery";
import * as project from "static/libs/project";

window['tomato'] = tomato;

let indexCmd = new tomato.Cmd(location.pathname,document.title,true);

indexCmd.redo = indexCmd.execute = function(){
    let app:project.Application = tomato.application as project.Application;
    app.changeContent(app.firstArticle,app.firstBanner,app.firstNav,false);
    this.success();
}

tomato.setConfig({
    application: new project.Application(indexCmd),
});


export = project.Application;
